package com.evergent.corejava.generalprogramming;

public class Nloop4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k=1;
		for(int i=1;i<=5;i++) {
			System.out.println();
			for(int j=1;j<=5;j++) {
				System.out.print(" "+k);
				k++;
			}
			
		}

	}

}
